(2, 'Header1', 'header', '<form action=\"AYARheader1.php\" method=\"POST\" enctype=\"multipart/form-data\" >\r\n<table border=\"0\" class=\"ayar_table\">\r\n<tr>\r\n<td>Başlık Giriniz:</td>\r\n<td><input type=\"text\" class=\"ayar_textbox\" name=\"header1_yazi\" id=\"header1_yazi\"/></td>\r\n</tr>\r\n<tr>\r\n<td>Başlık Yazı Fontu:</td>\r\n<td>\r\n<select name=\"header1_yazim_fontu\" class=\"ayar_select\">\r\n<option value=\"Times New Roman\">Times New Roman</option>\r\n<option value=\"Arial\">Arial</option>\r\n<option value=\"Serif\">serif</option>\r\n<option value=\"Comic Sans MS\">Comic Sans MS</option>\r\n</select>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>Başlık Renk:</td>\r\n<td><input class=\"jscolor\" name=\"header1_yazim_renk\" value=\"ab2567\"></td>\r\n</tr>\r\n<tr>\r\n<td>Resim Seç:</td>\r\n<td><input type=\"file\" name=\"header1_resim\" id=\"header1_resim\" accept=\"image/*\" onchange=\"loadFile(event)\"/><img src=\"komp/content1.png\" id=\"sky\" width=\"0\" height=\"0\"/></td>\r\n</tr>\r\n<tr>\r\n<td>Resim Genişlik(Piksel olarak):</td>\r\n<td><input type=\"number\" class=\"ayar_textbox_number\" min=\"1\" max=\"420\"name=\"header1_resim_genislik\" id=\"header1_resim_genislik\"/> Oranla: <input type=\"checkbox\" id=\"xxx\" name=\"xxx\" onclick=\"calc()\" checked/></td>\r\n</tr>\r\n<tr>\r\n<td>Resim Yükseklik(Piksel olarak):</td>\r\n<td><input type=\"number\" class=\"ayar_textbox_number\"  min=\"1\" max=\"270\" name=\"header1_resim_yükseklik\" id=\"header1_resim_yükseklik\"/></td></tr>\r\n<tr>\r\n<td>Resim daire olsun:</td>\r\n<td><input type=\"checkbox\" name=\"header1_resim_daire\" value=\"border-radius:50%;\"></td>\r\n</tr>\r\n<tr>\r\n<td>Resimde gölge efekti olsun:</td>\r\n<td><input type=\"checkbox\" name=\"header1_resim_golge\" value=\"-webkit-box-shadow: 10px 10px 5px 0px rgba(0,0,0,0.75); -moz-box-shadow: 10px 10px 5px 0px rgba(0,0,0,0.75); box-shadow: 10px 10px 5px 0px rgba(0,0,0,0.75);\"></td>\r\n</tr>\r\n<tr>\r\n<td>Arkaplan Renk:</td>\r\n<td><input class=\"jscolor\" name=\"header1_arkaplan\" value=\"ab2567\"></td>\r\n</tr>\r\n<tr>\r\n<td>Arkaplan Transparan Olsun:</td>\r\n<td><input type=\"checkbox\" name=\"header1_arkaplan\" value=\"transparent\"></td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"2\" style=\"\"><button name=\"sayfa_ekle\" onclick=\"return check();\" class=\"ayar_button\">Ekle</button></td>\r\n</tr>\r\n</table>\r\n</form>\r\n\r\n<script type=\"text/javascript\">\r\nfunction check()\r\n{\r\n    if (document.getElementById(\'header1_yazi\').value==\"\"\r\n        || document.getElementById(\'header1_yazi\').value==undefined)\r\n    {\r\n        alert (\"Lütfen başlık giriniz..\");\r\n        return false;\r\n    }else if (document.getElementById(\'header1_resim\').value==\"\"\r\n        || document.getElementById(\'header1_resim\').value==undefined)\r\n    {\r\n        alert (\"Lütfen resim seçiniz..\");\r\n        return false;\r\n    }else if (document.getElementById(\'header1_resim_genislik\').value==\"\"\r\n        || document.getElementById(\'header1_resim_genislik\').value==undefined)\r\n    {\r\n        alert (\"Lütfen genişlik giriniz..\");\r\n        return false;\r\n    }else if (document.getElementById(\'header1_resim_yükseklik\').value==\"\"\r\n        || document.getElementById(\'header1_resim_yükseklik\').value==undefined)\r\n    {\r\n        alert (\"Lütfen genişlik giriniz..\");\r\n        return false;\r\n    }\r\n        return true;\r\n    }\r\n</script>\r\n\r\n<script>\r\n	document.getElementById(\"header1_resim_genislik\").addEventListener(\"input\", oranla);\r\n\r\nfunction oranla() {\r\nif (document.getElementById(\'xxx\').checked && !document.getElementById(\'header1_resim\').value==\"\") \r\n  {\r\n    var myImg = document.querySelector(\"#sky\");\r\n	  var x = document.getElementById(\"header1_resim_genislik\").value;\r\n        var realWidth = myImg.naturalWidth;\r\n        var realHeight = myImg.naturalHeight;\r\n		var oran=realWidth/realHeight;\r\n\r\n		    document.getElementById(\"header1_resim_yükseklik\").value = Math.ceil(x/oran);\r\n			}\r\n}\r\nfunction calc(){\r\n  if (document.getElementById(\'xxx\').checked && !document.getElementById(\'header1_resim\').value==\"\") \r\n  {\r\n        var myImg = document.querySelector(\"#sky\");\r\n	  var x = document.getElementById(\"header1_resim_genislik\").value;\r\n        var realWidth = myImg.naturalWidth;\r\n        var realHeight = myImg.naturalHeight;\r\n		var oran=realWidth/realHeight;\r\n\r\n		    document.getElementById(\"header1_resim_yükseklik\").value = Math.ceil(x/oran);\r\n  }\r\n}\r\n var loadFile = function(event) {\r\n    var output = document.getElementById(\'sky\');\r\n    output.src = URL.createObjectURL(event.target.files[0]);\r\n    output.onload = function() {\r\n      URL.revokeObjectURL(output.src) // free memory\r\n    }\r\n  };\r\n</script>', '<?php\r\n$yazilacak_kod=\"<div style=\\\"background-color:#$header1_arkaplan &nbsp;;\\\">\r\n<center>\r\n<table border=\\\"0px\\\" cellpadding=\\\"20\\\" style=\\\"word-wrap:break-word;\\\">\r\n<tr>\r\n<td>\r\n<center>\r\n<a href=\\\"?sayfa=content\\\"><img src=\\\"resimler/$header1_resim &nbsp;\\\" width=\\\"$header1_resim_genislik &nbsp; px\\\" height=\\\"$header1_resim_yükseklik &nbsp; px\\\" style=\\\"$header1_resim_daire &nbsp;  $header1_resim_golge &nbsp;\\\"/></a>\r\n</center>\r\n</td></tr><tr>\r\n<td style=\\\"word-wrap:break-word;\\\">\r\n<center>\r\n<a href=\\\"?sayfa=content\\\" style=\\\"text-decoration:none;\\\"><h1 style=\\\"font-family:$header1_yazim_fontu &nbsp;; color:#$header1_yazim_renk &nbsp;;\\\">$header1_yazi &nbsp;</h1></a>\r\n</center>\r\n</td>\r\n</tr>\r\n</table>\r\n</center>\r\n</div>\";\r\n\r\n?>', 'komp/header1.png', ''),
