ner(\"input\", oranla);\r\n\r\nfunction oranla() {\r\nif (document.getElementById(\'xxx\').checked && !document.getElementById(\'content13_resim\').value==\"\") \r\n  {\r\n    var myImg = document.querySelector(\"#sky\");\r\n	  var x = document.getElementById(\"content13_resim_genislik\").value;\r\n        var realWidth = myImg.naturalWidth;\r\n        var realHeight = myImg.naturalHeight;\r\n		var oran=realWidth/realHeight;\r\n\r\n		    document.getElementById(\"content13_resim_yükseklik\").value = Math.ceil(x/oran);\r\n			}\r\n}\r\nfunction calc(){\r\n  if (document.getElementById(\'xxx\').checked && !document.getElementById(\'content13_resim\').value==\"\") \r\n  {\r\n        var myImg = document.querySelector(\"#sky\");\r\n	  var x = document.getElementById(\"content13_resim_genislik\").value;\r\n        var realWidth = myImg.naturalWidth;\r\n        var realHeight = myImg.naturalHeight;\r\n		var oran=realWidth/realHeight;\r\n\r\n		    document.getElementById(\"content13_resim_yükseklik\").value = Math.ceil(x/oran);\r\n  }\r\n}\r\n\r\n var loadFile = function(event) {\r\n    var output = document.getElementById(\'sky\');\r\n    output.src = URL.createObjectURL(event.target.files[0]);\r\n    output.onload = function() {\r\n      URL.revokeObjectURL(output.src) // free memory\r\n    }\r\n  };\r\n</script>', '<?php echo \'<div style=\"background-color:\'.$content13_arkaplan.\';\">\'; ?> \r\n<center> \r\n<table border=\"0px\" cellpadding=\"10\"> \r\n<tr> \r\n<td style=\"word-wrap:break-word;\"><h3><?php echo \'<a style=\"color:\'.$content13_yazim_rengi.\'; font-family: \'.$content13_yazim_fontu.\';  font-size:\'.$content13_yaziboyutu.\'; \">\'.$content13_yazim.\'</a>\'; ?></h3></td> \r\n<td> <?php echo \'<img src=\"resimler/\'.$content13_resim.\'\" width=\"\'.$content13_resim_genislik.\'px\" height=\"\'.$content13_resim_yükseklik.\'px\" style=\"\'.$content13_resim_daire.\' \'.$content13_resim_golge.\'\"/>\'; ?> </td> \r\n</tr> \r\n</table> \r\n</center> \r\n</div>',